import React, {useState} from "react";
import memesData from "../memesData.js"

export default function Form() {
   const [meme, setMeme] = useState({
      topText: "",
      bottomText: "",
      image: "https://i.imgflip.com/26am.jpg"
   });
   const [memeImages, setMemeImages] = useState(memesData);

   function getMemeImage() {
      const memeArray = memeImages.data.memes;
      const randomNumber = Math.floor(Math.random() * memeArray.length);
      const meme = memeArray[randomNumber].url;
      
      setMeme(prevMeme => ({
         ...prevMeme,
         image: memeImage
      }));
   }
   
   return (
      <main>
         <div className='form'>
            <input className='form-input' type='text' placeholder='Top'/>
            <input className='form-input' type='text' placeholder='Bottom'/>
            <button onClick={getMemeImage} className='form-button'>Get meme</button>
         </div>
         <img className='meme-image' src={memeImage}/>
      </main>
   )
}
