import React, {useState} from "react";
import memesData from "../memesData.js"

export default function Form() {
   let [memeImage, setMemeImage] = useState();

   function getImage() {
      const memesArray = memesData.data.memes;
      const randomNumber = Math.floor(Math.random() * memesArray.length);
      
      memeImage = memesArray[randomNumber].url;
      setMemeImage(memeImage);
   }
   
   return (
      <main>
         <div className='form'>
            <input className='form-input' type='text' placeholder='Top'/>
            <input className='form-input' type='text' placeholder='Bottom'/>
            <button onClick={getImage} className='form-button'>Get meme</button>
         </div>
         <img className='m' src={memeImage}/>
      </main>
   )
}
